import React, { useEffect, useState } from 'react'
import '../index.css'
import { api } from '../utils/Api'
import Header from './Header'
import Footer from './Footer'
import { PopupWithForm } from './PopupWithForm.js'
import { EditProfilePopup } from './EditProfilePopup.js';
import { ImagePopup } from './ImagePopup.js'
import { Main } from './Main.js'
import {CurrentUserContext} from '../contexts/CurrentUserContext'

function App () {
  const [currentUser, setCurrentUser] = useState({})
  const [cards, setCards] = useState([])
  const [selectedCard, setSelectedCard] = useState({})
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
  const [isFullImagePopupOpen, setFullImagePopupOpen] = useState(false)
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    Promise.all([api.getInfo(), api.getInitialCards()])
.then(([dataUser, resCard]) => {
 setCurrentUser(dataUser)
 setCards(resCard)
}, )
.catch((error) => console.log(`Ошибка: ${error}`));
  },[])

  function handleCardLike(card) {
     // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);
     // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLike(card._id, isLiked).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    }).catch((error) => console.log(`Ошибка: ${error}`));
}

function handleCardDelete(card) {
  api.deleteCard(card._id).then(()=>{
    setCards(cards => cards.filter((c) => c._id !== card._id))
  }).catch((error) => console.log(`Ошибка: ${error}`));
 }

 const handleUpdateUser = (info) => {
  setIsLoading(true);
    api.changeUserInfo(info)
    .then((newВфеу) => {
      setCurrentUser(newUser)
      closeAllPopups()
  })
  .catch((error) => console.log(`Ошибка: ${error}`))
  .finally(() => setIsLoading(false));
}

  function handleEditProfileClick () {
    setIsEditProfilePopupOpen(true)
  }

  function handleEditAvatarClick () {
    setIsEditAvatarPopupOpen(true)
  }

  function handleAddPlaceClick () {
    setIsAddPlacePopupOpen(true)
  }

  function handleCardClick (card) {
    setSelectedCard(card)
    setFullImagePopupOpen(true)
  }

  function closeAllPopups () {
    setIsEditProfilePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setSelectedCard({})
  }
  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className='page'>
      <Header />
      <Main
        onAddPlace={handleAddPlaceClick}
        onCardClick={handleCardClick}
        cards={cards}
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onCardLike={handleCardLike}
        onCardDelete={handleCardDelete}
      />
      <Footer />
      <EditProfilePopup
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        onUpdateUser={handleUpdateUser}
        isLoading={isLoading}
        />

      <PopupWithForm
        name='popup-add'
        title='Новое место'
        text='Создать'
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <fieldset className='form__fieldset'>
          <input
            type='text'
            name='place'
            id='image-input'
            className='form__input form__input_information_image'
            placeholder='Название'
            minLength={2}
            maxLength={30}
            required
          />
          <span className='form__span-error image-input-error' />
          <input
            type='url'
            name='link'
            id='link-input'
            className='form__input form__input_information_link'
            placeholder='Ссылка на картинку'
            required
          />
          <span className='form__span-error link-input-error' />
        </fieldset>
      </PopupWithForm>

      <PopupWithForm
        name='popup-avatar'
        title='Обновить аватар'
        text='Сохранить'
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <fieldset className='form__fieldset'>
          <input
            type='url'
            name='link'
            id='avatar-input'
            className='form__input'
            placeholder='Ссылка на аватар'
            required
          />
          <span className='form__span-error avatar-input-error' />
        </fieldset>
      </PopupWithForm>

      <ImagePopup
        card={selectedCard}
        isOpen={isFullImagePopupOpen}
        onClose={closeAllPopups}
      />
    </div>
    </CurrentUserContext.Provider>
  )
}

export default App
