import {PopupWithForm} from './PopupWithForm.js';
import { useState, useEffect, useContext } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';

function EditProfilePopup({isOpen, onClose, onUpdateUser, isLoading}) {
  const currentUser = useContext(CurrentUserContext)
  const[userName, setUserName] = useState('')
  const[userAbout, setUserAbout] = useState('')

  useEffect(() => {
    setUserName(currentUser.name);
    setUserAbout(currentUser.about);
  }, [currentUser, isOpen]);

  function handleSubmit(e) {
    e.preventDefault();
    onUpdateUser({
      name: userName,
      about: userAbout,
    });
  }

  function handleUserName(e) {
    setUserName(e.target.value);
  }

  function handleUserAbout(e) {
    setUserAbout(e.target.value);
  }

  return (
    <PopupWithForm
    name={'popup-edit'}
    title={'Редактировать профиль'}



    isOpen={isOpen}
    //       onClose={onClose}


    //       onSubmit={handleSubmit}
    //       isLoading={isLoading}
    //       buttonText="Cохранить"
    //       loadingText={'Сохранение...'}
  >
    <fieldset className='form__fieldset'>
      <input
        type='text'
        name='name'
        id='name-input'
        className='form__input form__input_information_name'
        placeholder='Имя'
        minLength={2}
        maxLength={40}
        required
      />
      <span className='form__span-error name-input-error' />
      <input
        type='text'
        name='about'
        id='job-input'
        className='form__input form__input_information_job'
        placeholder='О себе'
        minLength={2}
        maxLength={200}
        required
      />
      <span className='form__span-error job-input-error' />
    </fieldset>
  </PopupWithForm>

