import React from 'react';
import { UsePopupClose } from './UsePopupClose';
import { PopupWithForm } from './PopupWithForm'

const InfoTooltip = (props) => {
  const { isSuccess, isOpen, onClose,} = props;
  UsePopupClose(isOpen, onClose);
  return (
    <div className={`popup popup_opened ${isOpen ? 'popup_opened' : ''}`}>
      <div className="popup__container">
        <button
          type="button"
          className={`popup__close`}
          onClick={onClose}
        ></button>
        <div className={`popup__icon-tooltip-sucсess`
        // ${isSuccess ? 'success' : 'error'}`
        }></div>
        <h2 className=" popup__title_center">
          {/* {isSuccess ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Попробуйте ещё раз.'} */}
          Вы успешно зарегистрировались!

          </h2>
      </div>
    </div>
  );

};

export { InfoTooltip };
